/* Mobile First Approach */

/* Base styles (mobile) */
.container {
    width: 100%;
    padding: {{mobilePadding}};
}

{{#if includeTablet}}
/* Tablet (768px and up) */
@media (min-width: 768px) {
    .container {
        max-width: {{tabletMaxWidth}};
        padding: {{tabletPadding}};
    }
}
{{/if}}

{{#if includeDesktop}}
/* Desktop (1024px and up) */
@media (min-width: 1024px) {
    .container {
        max-width: {{desktopMaxWidth}};
        padding: {{desktopPadding}};
    }
}
{{/if}}

{{#if includeLargeDesktop}}
/* Large Desktop (1440px and up) */
@media (min-width: 1440px) {
    .container {
        max-width: {{largeDesktopMaxWidth}};
    }
}
{{/if}}

{{#if includeOrientation}}
/* Orientation */
@media (orientation: landscape) {
    .landscape-layout {
        display: flex;
        flex-direction: row;
    }
}

@media (orientation: portrait) {
    .portrait-layout {
        display: flex;
        flex-direction: column;
    }
}
{{/if}}

{{#if includePrint}}
/* Print styles */
@media print {
    .no-print {
        display: none;
    }

    body {
        font-size: 12pt;
        line-height: 1.5;
        color: #000;
        background: #fff;
    }

    a[href]:after {
        content: " (" attr(href) ")";
    }
}
{{/if}}

{{#if includeDarkMode}}
/* Dark Mode */
@media (prefers-color-scheme: dark) {
    body {
        background: {{darkBackground}};
        color: {{darkColor}};
    }

    .card {
        background: {{darkCardBackground}};
        border-color: {{darkBorderColor}};
    }
}
{{/if}}

{{#if includeReducedMotion}}
/* Reduced Motion */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}
{{/if}}

{{#if includeHighContrast}}
/* High Contrast */
@media (prefers-contrast: high) {
    body {
        background: #000;
        color: #fff;
    }

    a {
        border-bottom: 2px solid #fff;
    }
}
{{/if}}

{{#if includeHover}}
/* Hover support */
@media (hover: hover) {
    .hover-effect:hover {
        transform: scale(1.05);
    }
}

@media (hover: none) {
    .hover-effect:active {
        transform: scale(1.05);
    }
}
{{/if}}

{{#if includeTouch}}
/* Touch devices */
@media (pointer: coarse) {
    .touch-target {
        min-height: 44px;
        min-width: 44px;
    }
}
{{/if}}

{{#if includeCustomBreakpoints}}
/* Custom Breakpoints */
@media (min-width: {{customBreakpoint1}}px) {
    .custom-breakpoint-1 {
        /* Styles */
    }
}

@media (min-width: {{customBreakpoint2}}px) {
    .custom-breakpoint-2 {
        /* Styles */
    }
}
{{/if}}

{{#if includeMaxWidth}}
/* Max Width Queries */
@media (max-width: {{maxWidth}}px) {
    .mobile-only {
        display: block;
    }

    .desktop-only {
        display: none;
    }
}
{{/if}}

{{#if includeRange}}
/* Range Queries */
@media (min-width: {{minWidth}}px) and (max-width: {{maxWidth}}px) {
    .range-styles {
        /* Styles for specific range */
    }
}
{{/if}}
