/* Mobile First Approach */

/* Base styles (mobile) */
.container {
    width: 100%;
    padding: 1rem;
    margin: 0 auto;
}

/* Tablet (768px and up) */
@media (min-width: 768px) {
    .container {
        max-width: 750px;
        padding: 2rem;
    }

    .grid {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        gap: 1.5rem;
    }
}

/* Desktop (1024px and up) */
@media (min-width: 1024px) {
    .container {
        max-width: 1200px;
        padding: 3rem;
    }

    .grid {
        grid-template-columns: repeat(3, 1fr);
        gap: 2rem;
    }
}

/* Large Desktop (1440px and up) */
@media (min-width: 1440px) {
    .container {
        max-width: 1400px;
    }

    .grid {
        grid-template-columns: repeat(4, 1fr);
    }
}

/* Orientation */
@media (orientation: landscape) {
    .header {
        height: 60px;
    }
}

@media (orientation: portrait) {
    .header {
        height: 80px;
    }
}

/* Print styles */
@media print {
    .no-print {
        display: none;
    }

    body {
        font-size: 12pt;
        line-height: 1.5;
    }
}
