import { BrowserRouter, Routes, Route, Link, Navigate, useNavigate, useParams, useLocation } from 'react-router-dom';

{{#if includeProtectedRoute}}
// Composant de route protégée
function ProtectedRoute({ children, isAuthenticated }) {
  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }
  return children;
}
{{/if}}

{{#if includeLayout}}
// Layout component
function Layout({ children }) {
  return (
    <div className="layout">
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/contact">Contact</Link>
      </nav>
      <main>{children}</main>
    </div>
  );
}
{{/if}}

// Page components
function Home() {
  return (
    <div>
      <h1>Home Page</h1>
      <p>Welcome to the home page</p>
    </div>
  );
}

function About() {
  return (
    <div>
      <h1>About Page</h1>
      <p>This is the about page</p>
    </div>
  );
}

{{#if includeDynamicRoute}}
// Page avec paramètres dynamiques
function UserProfile() {
  const { userId } = useParams();
  const location = useLocation();

  return (
    <div>
      <h1>User Profile: {userId}</h1>
      <p>Current path: {location.pathname}</p>
    </div>
  );
}
{{/if}}

{{#if includeNotFound}}
// Page 404
function NotFound() {
  return (
    <div>
      <h1>404 - Page Not Found</h1>
      <Link to="/">Go Home</Link>
    </div>
  );
}
{{/if}}

{{#if includeLogin}}
// Page de connexion
function Login() {
  const navigate = useNavigate();

  const handleLogin = () => {
    // Logique de connexion
    localStorage.setItem('isAuthenticated', 'true');
    navigate('/dashboard', { replace: true });
  };

  return (
    <div>
      <h1>Login</h1>
      <button onClick={handleLogin}>Login</button>
    </div>
  );
}
{{/if}}

{{#if includeLazyLoading}}
// Lazy loading des composants
import { lazy, Suspense } from 'react';

const LazyComponent = lazy(() => import('./LazyComponent'));

function LazyRoute() {
  return (
    <Suspense fallback={<div>Loading...</div>}>
      <LazyComponent />
    </Suspense>
  );
}
{{/if}}

// App component avec routes
function App() {
  const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';

  return (
    <BrowserRouter>
      {{#if includeLayout}}
      <Layout>
      {{/if}}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />

          {{#if includeDynamicRoute}}
          <Route path="/user/:userId" element={<UserProfile />} />
          {{/if}}

          {{#if includeLogin}}
          <Route path="/login" element={<Login />} />
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <Dashboard />
              </ProtectedRoute>
            }
          />
          {{/if}}

          {{#if includeLazyLoading}}
          <Route path="/lazy" element={<LazyRoute />} />
          {{/if}}

          {{#if includeNotFound}}
          <Route path="*" element={<NotFound />} />
          {{/if}}
        </Routes>
      {{#if includeLayout}}
      </Layout>
      {{/if}}
    </BrowserRouter>
  );
}

export default App;
