code:
-- Index simple
CREATE INDEX idx_{{columnName}} ON {{tableName}} ({{columnName}});

-- Index composite (plusieurs colonnes)
CREATE INDEX idx_{{tableName}}_composite ON {{tableName}} ({{column1}}, {{column2}});

-- Index unique
CREATE UNIQUE INDEX idx_{{tableName}}_unique ON {{tableName}} ({{columnName}});

-- Index partiel (avec condition WHERE)
CREATE INDEX idx_{{tableName}}_active ON {{tableName}} ({{columnName}}) WHERE active = 1;

-- Index sur expression
CREATE INDEX idx_{{tableName}}_lower ON {{tableName}} (LOWER({{columnName}}));

-- Index avec tri (DESC)
CREATE INDEX idx_{{tableName}}_desc ON {{tableName}} ({{columnName}} DESC);

-- Index couvrant (covering index) - inclut les colonnes supplémentaires
CREATE INDEX idx_{{tableName}}_covering ON {{tableName}} ({{column1}}) INCLUDE ({{column2}}, {{column3}});

-- Index pour recherche full-text (MySQL)
CREATE FULLTEXT INDEX idx_{{tableName}}_fulltext ON {{tableName}} ({{columnName}});

-- Index pour recherche full-text (PostgreSQL)
CREATE INDEX idx_{{tableName}}_fulltext ON {{tableName}} USING GIN (to_tsvector('english', {{columnName}}));

-- Index partiel avec plusieurs conditions
CREATE INDEX idx_{{tableName}}_filtered ON {{tableName}} ({{columnName}}) 
WHERE active = 1 AND deleted_at IS NULL;

-- Index sur clé étrangère (améliore les JOINs)
CREATE INDEX idx_{{tableName}}_fk ON {{tableName}} ({{foreignKeyColumn}});

-- Index pour tri et filtrage combinés
CREATE INDEX idx_{{tableName}}_sort_filter ON {{tableName}} ({{filterColumn}}, {{sortColumn}} DESC);

-- Vérifier les index existants
-- MySQL
SHOW INDEXES FROM {{tableName}};

-- PostgreSQL
SELECT indexname, indexdef FROM pg_indexes WHERE tablename = '{{tableName}}';

-- SQL Server
SELECT name, type_desc FROM sys.indexes WHERE object_id = OBJECT_ID('{{tableName}}');

-- Supprimer un index
DROP INDEX idx_{{columnName}} ON {{tableName}};

-- Analyser l'utilisation des index (PostgreSQL)
EXPLAIN ANALYZE SELECT * FROM {{tableName}} WHERE {{columnName}} = 'value';

-- Reconstruire un index (SQL Server)
ALTER INDEX idx_{{columnName}} ON {{tableName}} REBUILD;

-- Réorganiser un index (SQL Server)
ALTER INDEX idx_{{columnName}} ON {{tableName}} REORGANIZE;
