/* Container Flexbox de base */
.flex-container {
    display: flex;
    flex-direction: row; /* row | column | row-reverse | column-reverse */
    flex-wrap: wrap; /* nowrap | wrap | wrap-reverse */
    justify-content: center; /* flex-start | flex-end | center | space-between | space-around | space-evenly */
    align-items: center; /* flex-start | flex-end | center | baseline | stretch */
    align-content: center; /* Pour plusieurs lignes */
    gap: 1rem;
}

/* Centrer verticalement et horizontalement */
.center {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
}

/* Layout en colonnes égales */
.equal-columns {
    display: flex;
}

.equal-columns > * {
    flex: 1;
}

/* Layout avec sidebar fixe */
.layout-with-sidebar {
    display: flex;
}

.sidebar {
    flex: 0 0 250px; /* Ne grandit pas, ne rétrécit pas, largeur de 250px */
}

.main-content {
    flex: 1; /* Prend tout l'espace restant */
}

/* Cards en grille responsive */
.cards-container {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
}

.card {
    flex: 1 1 300px; /* Min 300px, peut grandir et rétrécir */
    max-width: 400px;
}

/* Navigation horizontale */
.nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-item {
    flex: 0 1 auto;
}

/* Footer collé en bas */
.page-wrapper {
    display: flex;
    flex-direction: column;
    min-height: 100vh;
}

.main {
    flex: 1;
}

.footer {
    flex: 0 0 auto;
}
