/* Linear Gradient - Dégradé linéaire */
.linear-gradient {
    background: linear-gradient({{gradientDirection}}, {{color1}}, {{color2}}{{#if color3}}, {{color3}}{{/if}}{{#if color4}}, {{color4}}{{/if}});
    {{#if includeAnimation}}
    background-size: 200% 200%;
    animation: gradientShift {{animationDuration}}s ease infinite;
    {{/if}}
}

{{#if includeRadial}}
/* Radial Gradient - Dégradé radial */
.radial-gradient {
    background: radial-gradient(circle at {{radialPosition}}, {{color1}}, {{color2}});
}
{{/if}}

{{#if includeConic}}
/* Conic Gradient - Dégradé conique */
.conic-gradient {
    background: conic-gradient(from {{conicAngle}}deg, {{color1}}, {{color2}}, {{color3}}, {{color1}});
    {{#if includeAnimation}}
    animation: rotate {{animationDuration}}s linear infinite;
    {{/if}}
}
{{/if}}

{{#if includeRepeating}}
/* Repeating Gradient */
.repeating-gradient {
    background: repeating-linear-gradient(
        {{gradientDirection}},
        {{color1}} {{stop1}}%,
        {{color2}} {{stop2}}%
    );
}
{{/if}}

{{#if includeTextGradient}}
/* Text Gradient */
.text-gradient {
    background: linear-gradient({{gradientDirection}}, {{color1}}, {{color2}});
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
    color: transparent;
}
{{/if}}

{{#if includeAnimation}}
/* Animations */
@keyframes gradientShift {
    0% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
    }
}

@keyframes rotate {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}
{{/if}}

/* Exemples de dégradés prédéfinis */
{{#if includePresets}}
.sunset-gradient {
    background: linear-gradient(to right, #ff6b6b, #ffa500, #ffd700);
}

.ocean-gradient {
    background: linear-gradient(to bottom, #667eea, #764ba2);
}

.forest-gradient {
    background: linear-gradient(135deg, #134e5e, #71b280);
}

.fire-gradient {
    background: linear-gradient(to right, #f12711, #f5af19);
}
{{/if}}
