code:
{{#if includeFade}}
/* Fade In */
@keyframes fadeIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

.fade-in {
    animation: fadeIn {{duration}}s ease-in-out;
}

/* Fade Out */
@keyframes fadeOut {
    from {
        opacity: 1;
    }
    to {
        opacity: 0;
    }
}

.fade-out {
    animation: fadeOut {{duration}}s ease-in-out;
}
{{/if}}

{{#if includeSlide}}
/* Slide In */
@keyframes slideInLeft {
    from {
        transform: translateX(-100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.slide-in-left {
    animation: slideInLeft {{duration}}s ease-out;
}

@keyframes slideInRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.slide-in-right {
    animation: slideInRight {{duration}}s ease-out;
}

@keyframes slideInUp {
    from {
        transform: translateY(100%);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.slide-in-up {
    animation: slideInUp {{duration}}s ease-out;
}

@keyframes slideInDown {
    from {
        transform: translateY(-100%);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

.slide-in-down {
    animation: slideInDown {{duration}}s ease-out;
}
{{/if}}

{{#if includeRotate}}
/* Rotate */
@keyframes rotate {
    from {
        transform: rotate(0deg);
    }
    to {
        transform: rotate(360deg);
    }
}

.rotate {
    animation: rotate {{duration}}s linear infinite;
}

@keyframes rotateIn {
    from {
        transform: rotate(-200deg);
        opacity: 0;
    }
    to {
        transform: rotate(0deg);
        opacity: 1;
    }
}

.rotate-in {
    animation: rotateIn {{duration}}s ease-out;
}
{{/if}}

{{#if includeScale}}
/* Scale */
@keyframes scaleIn {
    from {
        transform: scale(0);
        opacity: 0;
    }
    to {
        transform: scale(1);
        opacity: 1;
    }
}

.scale-in {
    animation: scaleIn {{duration}}s ease-out;
}

@keyframes scaleOut {
    from {
        transform: scale(1);
        opacity: 1;
    }
    to {
        transform: scale(0);
        opacity: 0;
    }
}

.scale-out {
    animation: scaleOut {{duration}}s ease-out;
}

@keyframes pulse {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale({{pulseScale}});
    }
}

.pulse {
    animation: pulse {{duration}}s ease-in-out infinite;
}
{{/if}}

{{#if includeBounce}}
/* Bounce */
@keyframes bounce {
    0%, 20%, 50%, 80%, 100% {
        transform: translateY(0);
    }
    40% {
        transform: translateY(-{{bounceHeight}}px);
    }
    60% {
        transform: translateY(-{{bounceHeight}}px / 2);
    }
}

.bounce {
    animation: bounce {{duration}}s infinite;
}

@keyframes bounceIn {
    0% {
        transform: scale(0.3);
        opacity: 0;
    }
    50% {
        transform: scale(1.05);
    }
    70% {
        transform: scale(0.9);
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

.bounce-in {
    animation: bounceIn {{duration}}s ease-out;
}
{{/if}}

{{#if includeShake}}
/* Shake */
@keyframes shake {
    0%, 100% {
        transform: translateX(0);
    }
    10%, 30%, 50%, 70%, 90% {
        transform: translateX(-{{shakeDistance}}px);
    }
    20%, 40%, 60%, 80% {
        transform: translateX({{shakeDistance}}px);
    }
}

.shake {
    animation: shake {{duration}}s;
}
{{/if}}

{{#if includeFlip}}
/* Flip */
@keyframes flipInX {
    from {
        transform: perspective(400px) rotateX(90deg);
        opacity: 0;
    }
    40% {
        transform: perspective(400px) rotateX(-20deg);
    }
    60% {
        transform: perspective(400px) rotateX(10deg);
    }
    80% {
        transform: perspective(400px) rotateX(-5deg);
    }
    to {
        transform: perspective(400px) rotateX(0deg);
        opacity: 1;
    }
}

.flip-in-x {
    animation: flipInX {{duration}}s;
}
{{/if}}

{{#if includeZoom}}
/* Zoom */
@keyframes zoomIn {
    from {
        transform: scale(0);
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    to {
        transform: scale(1);
        opacity: 1;
    }
}

.zoom-in {
    animation: zoomIn {{duration}}s ease-out;
}

@keyframes zoomOut {
    from {
        transform: scale(1);
        opacity: 1;
    }
    50% {
        opacity: 1;
    }
    to {
        transform: scale(0);
        opacity: 0;
    }
}

.zoom-out {
    animation: zoomOut {{duration}}s ease-out;
}
{{/if}}

{{#if includeCustom}}
/* Animation personnalisée */
@keyframes {{customAnimationName}} {
    0% {
        {{customKeyframe0}}
    }
    50% {
        {{customKeyframe50}}
    }
    100% {
        {{customKeyframe100}}
    }
}

.{{customAnimationClass}} {
    animation: {{customAnimationName}} {{duration}}s {{timingFunction}} {{iterationCount}};
}
{{/if}}

/* Utilisation avec animation-delay */
{{#if includeDelay}}
.animated-delay-1 {
    animation-delay: 0.1s;
}

.animated-delay-2 {
    animation-delay: 0.2s;
}

.animated-delay-3 {
    animation-delay: 0.3s;
}
{{/if}}
