/* Transformations de base */
.transform-translate {
    transform: translate(50px, 100px);
}

.transform-rotate {
    transform: rotate(45deg);
}

.transform-scale {
    transform: scale(1.5);
}

.transform-skew {
    transform: skew(20deg, 10deg);
}

/* Transformations combinées */
.transform-combined {
    transform: translate(50px, 50px) rotate(45deg) scale(1.2);
}

/* Transform 3D */
.transform-3d {
    transform: perspective(1000px) rotateX(45deg) rotateY(30deg);
    transform-style: preserve-3d;
}

.transform-rotate-y {
    transform: rotateY(180deg);
    backface-visibility: hidden;
}

/* Transitions */
.transition-basic {
    transition: all 0.3s ease;
}

.transition-property {
    transition-property: transform, opacity;
    transition-duration: 0.3s, 0.5s;
    transition-timing-function: ease-in-out, linear;
}

/* Timing functions */
.ease { transition-timing-function: ease; }
.ease-in { transition-timing-function: ease-in; }
.ease-out { transition-timing-function: ease-out; }
.ease-in-out { transition-timing-function: ease-in-out; }
.linear { transition-timing-function: linear; }
.cubic-bezier { transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1); }

/* Hover effects */
.hover-scale:hover {
    transform: scale(1.1);
    transition: transform 0.3s ease;
}

.hover-rotate:hover {
    transform: rotate(5deg);
    transition: transform 0.3s ease;
}

.hover-lift:hover {
    transform: translateY(-10px);
    box-shadow: 0 10px 20px rgba(0,0,0,0.2);
    transition: all 0.3s ease;
}

/* Flip card effect */
.flip-card {
    perspective: 1000px;
}

.flip-card-inner {
    position: relative;
    width: 100%;
    height: 100%;
    transition: transform 0.6s;
    transform-style: preserve-3d;
}

.flip-card:hover .flip-card-inner {
    transform: rotateY(180deg);
}

.flip-card-front,
.flip-card-back {
    position: absolute;
    width: 100%;
    height: 100%;
    backface-visibility: hidden;
}

.flip-card-back {
    transform: rotateY(180deg);
}

/* Transform origin */
.transform-origin-center {
    transform-origin: center center;
}

.transform-origin-top-left {
    transform-origin: top left;
}

.transform-origin-custom {
    transform-origin: 30% 70%;
}

/* Will-change pour performance */
.optimized-transform {
    will-change: transform;
    transform: translateZ(0);
}

/* Animation avec transform */
@keyframes slideInFromLeft {
    from {
        transform: translateX(-100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.animate-slide-in {
    animation: slideInFromLeft 0.5s ease-out;
}
