/* Transition basique */
.basic-transition {
    transition: {{property}} {{duration}}s {{timingFunction}};
}

{{#if includeHover}}
/* Transition au survol */
.hover-transition {
    background: {{initialColor}};
    color: {{initialTextColor}};
    transition: all {{duration}}s {{timingFunction}};
}

.hover-transition:hover {
    background: {{hoverColor}};
    color: {{hoverTextColor}};
    transform: {{transform}};
}
{{/if}}

{{#if includeFocus}}
/* Transition au focus */
.focus-transition:focus {
    outline: none;
    border-color: {{focusColor}};
    box-shadow: 0 0 0 3px {{focusShadowColor}};
    transition: border-color {{duration}}s, box-shadow {{duration}}s;
}
{{/if}}

{{#if includeMultiple}}
/* Transitions multiples */
.multiple-transitions {
    transition:
        background {{duration}}s {{timingFunction}},
        color {{duration}}s {{timingFunction}},
        transform {{duration}}s {{timingFunction}},
        opacity {{duration}}s {{timingFunction}};
}
{{/if}}

{{#if includeDelay}}
/* Transition avec délai */
.delayed-transition {
    transition: {{property}} {{duration}}s {{timingFunction}} {{delay}}s;
}
{{/if}}

{{#if includeTransform}}
/* Transitions de transformation */
.transform-transition {
    transition: transform {{duration}}s {{timingFunction}};
}

.transform-transition:hover {
    transform: {{transformValue}};
}
{{/if}}

{{#if includeOpacity}}
/* Transition d'opacité (fade) */
.fade-transition {
    opacity: {{initialOpacity}};
    transition: opacity {{duration}}s {{timingFunction}};
}

.fade-transition:hover {
    opacity: {{finalOpacity}};
}
{{/if}}

{{#if includeHeight}}
/* Transition de hauteur (accordion) */
.height-transition {
    max-height: 0;
    overflow: hidden;
    transition: max-height {{duration}}s {{timingFunction}};
}

.height-transition.open {
    max-height: {{maxHeight}}px;
}
{{/if}}

{{#if includeWidth}}
/* Transition de largeur */
.width-transition {
    width: {{initialWidth}};
    transition: width {{duration}}s {{timingFunction}};
}

.width-transition.expanded {
    width: {{finalWidth}};
}
{{/if}}

{{#if includeColor}}
/* Transition de couleur */
.color-transition {
    background: {{initialColor}};
    transition: background {{duration}}s {{timingFunction}};
}

.color-transition:hover {
    background: {{finalColor}};
}
{{/if}}

{{#if includeCustomTiming}}
/* Timing functions personnalisées (cubic-bezier) */
.custom-timing {
    transition: {{property}} {{duration}}s cubic-bezier({{bezierX1}}, {{bezierY1}}, {{bezierX2}}, {{bezierY2}});
}
{{/if}}
