// Classe de base
class Animal {
    constructor(name, species) {
        this.name = name;
        this.species = species;
    }

    speak() {
        return `${this.name} makes a sound`;
    }

    static create(name, species) {
        return new Animal(name, species);
    }

    get info() {
        return `Name: ${this.name}, Species: ${this.species}`;
    }

    set name(newName) {
        if (newName.length < 2) {
            throw new Error('Name must be at least 2 characters');
        }
        this._name = newName;
    }

    get name() {
        return this._name;
    }
}

// Héritage
class Dog extends Animal {
    constructor(name, breed) {
        super(name, 'Canine');
        this.breed = breed;
    }

    speak() {
        return `${this.name} barks`;
    }

    fetch() {
        return `${this.name} fetches the ball`;
    }
}

// Classe avec méthodes privées (ES2022)
class BankAccount {
    #balance = 0;

    constructor(owner, initialBalance = 0) {
        this.owner = owner;
        this.#balance = initialBalance;
    }

    deposit(amount) {
        if (amount <= 0) {
            throw new Error('Amount must be positive');
        }
        this.#balance += amount;
        return this.#balance;
    }

    withdraw(amount) {
        if (amount > this.#balance) {
            throw new Error('Insufficient funds');
        }
        this.#balance -= amount;
        return this.#balance;
    }

    getBalance() {
        return this.#balance;
    }

    static createAccount(owner, initialBalance) {
        return new BankAccount(owner, initialBalance);
    }
}

// Classe abstraite (pattern)
class Shape {
    constructor(color) {
        if (this.constructor === Shape) {
            throw new Error('Shape is an abstract class');
        }
        this.color = color;
    }

    area() {
        throw new Error('Method area() must be implemented');
    }

    perimeter() {
        throw new Error('Method perimeter() must be implemented');
    }
}

class Circle extends Shape {
    constructor(color, radius) {
        super(color);
        this.radius = radius;
    }

    area() {
        return Math.PI * this.radius ** 2;
    }

    perimeter() {
        return 2 * Math.PI * this.radius;
    }
}

// Mixin pattern
const CanFly = {
    fly() {
        return `${this.name} is flying`;
    }
};

const CanSwim = {
    swim() {
        return `${this.name} is swimming`;
    }
};

class Duck extends Animal {
    constructor(name) {
        super(name, 'Bird');
    }
}

Object.assign(Duck.prototype, CanFly, CanSwim);

// Classe avec Symbol pour propriétés privées
const _private = Symbol('private');

class SecureClass {
    constructor() {
        this[_private] = {
            secret: 'hidden data'
        };
    }

    getSecret() {
        return this[_private].secret;
    }
}

// Singleton pattern
class Singleton {
    static instance = null;

    constructor() {
        if (Singleton.instance) {
            return Singleton.instance;
        }
        Singleton.instance = this;
    }

    static getInstance() {
        if (!Singleton.instance) {
            Singleton.instance = new Singleton();
        }
        return Singleton.instance;
    }
}

// Factory pattern avec classes
class AnimalFactory {
    static create(type, name) {
        switch (type) {
            case 'dog':
                return new Dog(name, 'Unknown');
            case 'cat':
                return new Cat(name);
            default:
                throw new Error(`Unknown animal type: ${type}`);
        }
    }
}

