// Debounce: Exécute la fonction après un délai d'inactivité
function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
        const later = () => {
            clearTimeout(timeout);
            func(...args);
        };
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
    };
}

// Throttle: Exécute la fonction au plus une fois par période
function throttle(func, limit) {
    let inThrottle;
    return function executedFunction(...args) {
        if (!inThrottle) {
            func.apply(this, args);
            inThrottle = true;
            setTimeout(() => inThrottle = false, limit);
        }
    };
}

// Debounce avec options avancées
function advancedDebounce(func, wait, options = {}) {
    let timeout;
    let maxWait;
    let maxWaitTimeout;
    let lastCallTime;

    const { leading = false, trailing = true, maxWait: maxWaitOption } = options;

    if (maxWaitOption) {
        maxWait = maxWaitOption;
    }

    function invokeFunc(...args) {
        return func.apply(this, args);
    }

    function leadingEdge(...args) {
        if (maxWaitOption) {
            lastCallTime = Date.now();
        }
        return leading ? invokeFunc(...args) : undefined;
    }

    function trailingEdge(...args) {
        timeout = undefined;
        if (trailing) {
            return invokeFunc(...args);
        }
    }

    function shouldInvoke() {
        const time = Date.now();
        const timeSinceLastCall = time - lastCallTime;

        return (
            lastCallTime === undefined ||
            timeSinceLastCall >= wait ||
            (maxWaitOption && timeSinceLastCall >= maxWait)
        );
    }

    return function debounced(...args) {
        const isInvoking = shouldInvoke();

        if (isInvoking) {
            if (timeout === undefined) {
                return leadingEdge(...args);
            }
            if (maxWaitOption) {
                timeout = setTimeout(trailingEdge, wait);
                return invokeFunc(...args);
            }
        }

        if (timeout === undefined) {
            timeout = setTimeout(trailingEdge, wait);
        }

        if (maxWaitOption && !maxWaitTimeout) {
            maxWaitTimeout = setTimeout(() => {
                if (shouldInvoke()) {
                    trailingEdge(...args);
                }
                maxWaitTimeout = undefined;
            }, maxWait);
        }
    };
}

// Utilisation
// Debounce simple
const debouncedSearch = debounce((query) => {
    console.log('Searching for:', query);
}, 300);

// Throttle simple
const throttledScroll = throttle(() => {
    console.log('Scroll event');
}, 100);

// Debounce avancé
const advancedDebounced = advancedDebounce(
    (value) => console.log('Value:', value),
    300,
    { leading: true, trailing: true, maxWait: 1000 }
);

// Exemple avec input
const searchInput = document.getElementById('search');
searchInput.addEventListener('input', (e) => {
    debouncedSearch(e.target.value);
});

// Exemple avec scroll
window.addEventListener('scroll', throttledScroll);
