class FormValidator {
    constructor(formId) {
        this.form = document.getElementById(formId);
        this.errors = {};
    }

    validate() {
        this.errors = {};
        const inputs = this.form.querySelectorAll('input, textarea, select');

        inputs.forEach(input => {
            const value = input.value.trim();
            const rules = input.dataset.rules ? input.dataset.rules.split('|') : [];

            rules.forEach(rule => {
                if (!this.checkRule(value, rule, input)) {
                    this.addError(input.name, this.getErrorMessage(rule));
                }
            });
        });

        return Object.keys(this.errors).length === 0;
    }

    checkRule(value, rule, input) {
        switch (rule) {
            case 'required':
                return value.length > 0;
            case 'email':
                return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);
            case 'min':
                const min = parseInt(input.dataset.min || '0');
                return value.length >= min;
            case 'max':
                const max = parseInt(input.dataset.max || '1000');
                return value.length <= max;
            default:
                return true;
        }
    }

    addError(field, message) {
        if (!this.errors[field]) {
            this.errors[field] = [];
        }
        this.errors[field].push(message);
    }

    getErrorMessage(rule) {
        const messages = {
            required: 'Ce champ est requis',
            email: 'Email invalide',
            min: 'Trop court',
            max: 'Trop long',
        };
        return messages[rule] || 'Erreur de validation';
    }

    displayErrors() {
        Object.keys(this.errors).forEach(field => {
            const input = this.form.querySelector(`[name="${field}"]`);
            if (input) {
                input.classList.add('error');
                const errorDiv = document.createElement('div');
                errorDiv.className = 'error-message';
                errorDiv.textContent = this.errors[field].join(', ');
                input.parentNode.appendChild(errorDiv);
            }
        });
    }
}

// Utilisation
const validator = new FormValidator('myForm');
if (validator.validate()) {
    // Soumettre le formulaire
} else {
    validator.displayErrors();
}
